import React,{useState} from 'react'
import {HeroBg,ImageBg,HeroP,HeroH1,HeroContainer,HeroBtnWrapper,ArrowForward,ArrowRight} from './HeroElements';
import {Button} from '../../ButtonElements';
import video from '../../videos/video.mp4';

const HeroSection = () => {
    const [hover, setHover] = useState(false);

    const onHover = () => {
        setHover(!hover);

    }

    return (
        <HeroContainer>
            <HeroBg>
                <ImageBg autoPlay loop muted src={video}/>
            </HeroBg>
            <HeroH1>upgrade your business with our analytics!skdjskldfjskldfjsdf</HeroH1>
            <HeroP>
                Hire us today, and get the help you need !kdjfksdjfsldkfjsdlfkj
            </HeroP>
            <h1>hello guys</h1>
            <HeroBtnWrapper>
                <Button to='signup' primary="true" dark="true" onMouseEnter={onHover} onMouseLeave={onHover}>Hire! {hover ? <ArrowForward/> : <ArrowRight/>}</Button>
            </HeroBtnWrapper>
            
        </HeroContainer>
    )
}

export default HeroSection;
